import React, { Component } from "react";
import FriendCard from "./components/FriendCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import friends from "./friends.json";
// import shuffle from "shuffle"


class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    friends,
    score: 0,
    highScore: 0,
    clickedArray: []
  };
  handleClick = id => {
    const shuffledArray = this.shuffleArray(friends);
    this.setState({ friends: shuffledArray });
    if (this.state.clickedArray.includes(id)) {
      this.setState({ score: 0, clickedArray: [], message: "Incorrect!! Game Over â˜¹ï¸ Click an image to start again!", shakeit: "true" });
    }
    else {
      this.setState({
        clickedArray: this.state.clickedArray.concat([id]),
        score: this.state.score + 1,
        message: "Correct!! ðŸ™‚",
        shakeit: "false"
      });
      // shuffleArray= () => {
      //   shuffle(this.state)
      //   this.setState({ friends });
    };
    endGame = () => {
      if (this.state.score > this.state.highscore) {
        this.setState({ highscore: this.state.score }, function () {
          console.log(this.state.highscore);
        });
      }
      this.state.cards.forEach(card => {
        card.count = 0;
      });
      this.setState({ score: 0 });
      return true;

    }
  }
  render() {
    return (
      <Wrapper>
        <Title>
          score = {this.handleScore}
          High Score= {this.highScore}
        </Title>
        {this.state.friends.map(friend => (
          <FriendCard
            // removeFriend={this.removeFriend}
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}
            handleClick={this.handleClick}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;
